package decisionMaking;

public class ElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 10;
		if(a>b) {
			System.out.println(a);
		}else if(a==b) {
			System.out.println("a is equals to b");
		}else {
			System.out.println(b);
		}
	}

}
